mapper.update:
update pscp_cms_menu 

	parent_id = #{parentId}, 
	name = #{name}, 
	url = #{url}, 
	perms = #{perms}, 
	type = #{type}, 
	icon = #{icon}, 
	order_num = #{orderNum}

,modified_time=now(),modified_user=#{modifiedUser}
where id = #{id}

mapper.delete:
update pscp_cms_menu set deleted=-1, modified_time=now(),modified_user=#{modifiedUser} where id in 

	#{id}

;
update pscp_cms_role_menu set deleted=-1, modified_time=now(),modified_user=#{modifiedUser}  where menu_id in 

	#{id}
