mapper.update:
update pscp_electrician_certificate
electrician_id = #{electricianId}, certificate_id = #{certificateId}, order_num = #{orderNum}, status = #{status}, remark = #{remark}, create_time = #{createTime}, create_user = #{createUser}, modified_time = #{modifiedTime}, modified_user = #{modifiedUser}, deleted = #{deleted}
where id = #{id} and deleted=0
