mapper.update:
update pscp_electrician_work_log
`electrician_work_order_id` = #{electricianWorkOrderId}, `work_order_id` = #{workOrderId}, `electrician_id` = #{electricianId}, `content` = #{content}, `company_id` = #{companyId}, `create_time` = #{createTime}, `create_user` = #{createUser}, `modified_time` = #{modifiedTime}, `modified_user` = #{modifiedUser}, `deleted` = #{deleted}
where id = #{id} and deleted=0

mapper.delete:
UPDATE pscp_electrician_work_log
SET deleted = -1, modified_time = now(), modified_user = ${modifiedUser}
WHERE id = #{value}

mapper.select:
SELECT * FROM pscp_electrician_work_log
WHERE deleted = 0
AND work_order_id = #{workOrderId}
ORDER BY create_time DESC

    limit #{limit} offset #{offset}

SELECT count(1) FROM pscp_electrician_work_log
WHERE deleted = 0
 AND work_order_id = #{workOrderId}
