mapper.select:
select * from pscp_member_bank_card where 1=1

		and uid = #{bean.uid}

	and  deleted=0 
order by create_time desc

	limit #{limit} offset #{offset}

select count(*) from pscp_member_bank_card  where 1=1

		and uid =#{uid}

 and deleted=0

mapper.update:
update pscp_member_bank_card
uid = #{uid}, bank_id = #{bankId}, bank_name = #{bankName}, bank_branch_name = #{bankBranchName}, account = #{account}, status = #{status}, province_id=#{provinceId}, city_id=#{cityId}, deleted = #{deleted}, remark = #{remark}, modified_time = now() , modified_user = #{modifiedUser}
where id = #{id} and deleted=0
