mapper.update:
update pscp_company
identifier = #{identifier}, member_id = #{memberId}, user_name = #{userName}, id_card_no = #{idCardNo}, id_card_front_img = #{idCardFrontImg}, id_card_back_img = #{idCardBackImg}, organization_code_img = #{organizationCodeImg}, name = #{name}, type= #{type}, legal_person = #{legalPerson}, business_licence = #{businessLicence}, business_licence_img = #{businessLicenceImg}, contacts = #{contacts}, mobile = #{mobile}, email = #{email}, phone = #{phone}, province_id = #{provinceId}, city_id = #{cityId}, area_id = #{areaId}, address = #{address}, website = #{website}, application_letter_fid = #{applicationLetterFid}, status = #{status}, remark = #{remark}, create_time = #{createTime}, create_user = #{createUser}, modified_time = #{modifiedTime}, modified_user = #{modifiedUser}, deleted = #{deleted}
where id = #{id} and deleted=0

mapper.delete:
UPDATE pscp_company
SET deleted = -1, modified_time = now(), modified_user = ${modifiedUser}
WHERE id = #{value}
